import {useState,useEffect} from 'react';
import {Navbar, Nav, Container} from 'react-bootstrap';
import navIcon1 from '../assets/img/nav-icon1.svg';
import navIcon2 from '../assets/img/nav-icon2.svg';
import navIcon3 from '../assets/img/nav-icon3.svg';
import {Download} from 'react-bootstrap-icons';
import 'animate.css';
import Axios from 'axios';
import FileDownload from 'js-file-download';
import {saveAs} from 'file-saver';

export const NavbarContainer = ()=>{
    const [activeLink,setActiveLink] = useState('home');
    const [isScrolled,setIsScrolled] = useState(false);

    useEffect(()=>{
        const onScroll = ()=>{
            if(window.scrollY > 50){
                setIsScrolled(true);
            }else{
                setIsScrolled(false);
            }
        }

        window.addEventListener('scroll',onScroll);

        return ()=> window.removeEventListener('scroll',onScroll);
    },[])

    const handleDownload = async(e)=>{
        e.preventDefault();
        const resumeData = await Axios({url:'http://localhost:5000/resume',method:'GET',responseType:'blob'});
        // saveAs(resumeData,'Download.pdf');
    }

    const handleUpdateActiveLink = (value) => {
        setActiveLink(value);
      }

    return(
    <Navbar expand="lg" className={isScrolled ? 'scrolled':''}>
    <Container>
      <Navbar.Toggle aria-controls="basic-navbar-nav" />
      <Navbar.Collapse id="basic-navbar-nav">
        <Nav className="me-auto">
          <Nav.Link href="#home" onClick = {()=> handleUpdateActiveLink('home')} className={activeLink === 'home' ? 'active navbar-link' : 'navbar-link'}>Home</Nav.Link>
          <Nav.Link href="#skills" onClick = {()=> handleUpdateActiveLink('skills')} className={activeLink === 'home' ? 'active navbar-link' : 'navbar-link'}>Skills</Nav.Link>
          <Nav.Link href="#projects" onClick = {()=> handleUpdateActiveLink('projects')} className={activeLink === 'home' ? 'active navbar-link' : 'navbar-link'}>Projects</Nav.Link>
        </Nav>
        <span className="navbar-text">
            <div className="social-icon">
                <a href="#"><img src={navIcon1} alt=""/></a>
                <a href="#"><img src={navIcon2} alt=""/></a>
                <a href="#"><img src={navIcon3} alt=""/></a>
            </div>

            <button  onClick={(e)=> handleDownload(e)}className="vvd" download><span>Download Resume <span className="download"><Download size={25} /></span></span></button>
        </span>
      </Navbar.Collapse>
    </Container>
  </Navbar>);
}
